import React, { useState } from 'react';
import './App.css';
import User, { UserInt } from './components/User';

const App: React.FC = () => {

  interface AllUsersInt {
    currentUser: UserInt;
    allUsers: Array<UserInt>
  }

  const [usersState, setUsersState] = useState<AllUsersInt>({
    currentUser: {
      name: "",
      age: "",
      job: "",
      deleteUser: () => { }
    },
    allUsers: []
  })

  const onChangeHandler = (e: React.ChangeEvent<HTMLInputElement>): void => {

    setUsersState({
      ...usersState,
      currentUser: {
        ...usersState.currentUser,
        [e.target.name]: e.target.value
      }
    })

  }

  const submitForm = (e: React.SyntheticEvent): void => {
    e.preventDefault();

    setUsersState({
      currentUser: {
        name: "",
        age: "",
        job: "",
        deleteUser: () => { }
      },
      allUsers: [
        ...usersState.allUsers,
        usersState.currentUser
      ]
    })
  }

  const deleteHandler = (index: number): void => {
    setUsersState({
      ...usersState,
      allUsers: usersState.allUsers.filter((user, i) => index !== i)
    })
  }

  const allUsers = usersState.allUsers.map((user, i) => (
    // <div key={i}>
    //   <p>{user.name}</p>
    //   <p>{user.age}</p>
    //   <p>{user.job}</p>
    //   <button onClick={() => deleteHandler(i)}>Delete user</button>
    // </div>
    <User
      key={i}
      name={user.name}
      age={user.age}
      job={user.job}
      deleteUser={() => deleteHandler(i)}
    />
  ))
  // console.log(usersState.currentUser);

  return (
    <div className="container">
      <h1>React with Typescript</h1>
      <form onSubmit={submitForm} className="card">
        <label htmlFor="userName">Name:</label>
        <input
          id="userName"
          type="text"
          name="name"
          value={usersState.currentUser.name}
          onChange={onChangeHandler}
        />

        <label htmlFor="userAge">Age:</label>
        <input
          id="userAge"
          type="number"
          name="age"
          value={usersState.currentUser.age}
          onChange={onChangeHandler}
        />

        <label htmlFor="userJob">Job:</label>
        <input
          required
          id="userJob"
          type="text"
          name="job"
          value={usersState.currentUser.job}
          onChange={onChangeHandler}
        />

        <button type="submit" className="">Add user</button>
      </form>
      { allUsers}
    </div>
  );
}

export default App;
